/**
Wrapper DataBlock Testing
a Wrapper DataBlock correspond to one or more category and contains one or more Records.
DataBlock = CAT + LEN + [FSPEC + items...] + [...] + ...
WrapperDataBlock = [CAT + LEN + RECORD + ...] + [ DATABLOCK ] + [...]
*/
type WrapperDataBlockTest struct {
	input          string // data block test input
	nbOfDataBlocks int
	err            error // error expected
	unRead         int   // the number of bytes not read.
}

func TestWrapperDataBlockDecode(t *testing.T) {
	// setup
	dataSet := []WrapperDataBlockTest{
		{
			// CAT048 + CAT034
			input:          " 220014f60836024eadd618940028200094008000",
			err:            nil,
			nbOfDataBlocks: 2,
			unRead:         0,
		},
		{
			// CAT062 + CAT065
			input:          "",
			err:            nil,
			nbOfDataBlocks: 2,
			unRead:         0,
		},
		{
			// CAT030 STR + CAT255 STR
			input:          " ff000ae008837e019d58",
			err:            nil,
			nbOfDataBlocks: 2,
			unRead:         0,
		},
		{
			// CAT002 + CAT001
			input:          "02000cf4083902105fb35b02010076f502083990002018aa134c06db08f000750290003b1595114104f15f470075029001b407b5115401ca25dc0075029000f212e514b2067fdc230075029000f508271713020875c90075029001050b78178e02743f7c007502900114090417b80190e4ca00750690010412e915bf079cba112000",
			err:            nil,
			nbOfDataBlocks: 2,
			unRead:         0,
		},
		{
			// empty
			input:          "",
			err:            io.EOF,
			nbOfDataBlocks: 0,
			unRead:         0,
		},
	}

	for _, row := range dataSet {
		// Arrange
		data, _ := util.HexStringToByte(row.input)
		w, _ := NewWrapperDataBlock()

		// Act
		unRead, err := w.Decode(data)

		// Assert
		if err != row.err {
			t.Errorf("MsgFailInValue: error: %s; Expected: %v", err, row.err)
		} else {
			t.Logf("MsgSuccessInValue: error: %v; Expected: %v", err, row.err)
		}
		if unRead != row.unRead {
			t.Errorf("MsgFailInValue: unRead = %v; Expected: %v", unRead, row.unRead)
		} else {
			t.Logf("MsgSuccessInValue: unRead = %v; Expected: %v", unRead, row.unRead)
		}
		if len(w.DataBlocks) != row.nbOfDataBlocks {
			t.Errorf("MsgFailInValue: nbOfDataBlocks = %v; Expected: %v", len(w.DataBlocks), row.nbOfDataBlocks)
		} else {
			t.Logf("MsgSuccessInValue: nbOfDataBlocks = %v; Expected: %v", len(w.DataBlocks), row.nbOfDataBlocks)
		}
	}
}

// DataBlock Testing
// a DataBlock correspond to one (only) category and contains one or more Records.
// DataBlock = CAT + LEN + [FSPEC + items...] + [...] + ...
type DataBlockTest struct {
	TestCaseName string
	input        string // data block test input
	nbOfRecords  int
	err          error // error expected
	unRead       int   // the number of bytes not read.
}

func TestDataBlockDecode(t *testing.T) {
	// setup
	dataSet := []DataBlockTest{
		{
			TestCaseName: "empty data block",
			input:        "",
			err:          io.EOF,
			nbOfRecords:  0,
			unRead:       0,
		},
		{
			TestCaseName: "CAT048 only category dataField",
			input:        "30",
			err:          io.EOF,
			nbOfRecords:  0,
			unRead:       0,
		},
		{
			TestCaseName: "CAT062",
			input:        "",
			err:          nil,
			nbOfRecords:  11,
			unRead:       0,
		},
		{
			TestCaseName: "CAT065",
			input:        "41000cf8090c0203424d730f",
			err:          nil,
			nbOfRecords:  1,
			unRead:       0,
		},
		{
			TestCaseName: "CAT048",
			input:        "",
			err:          nil,
			nbOfRecords:  5,
			unRead:       0,
		},
		{
			TestCaseName: "CAT048",
			input:        "",
			err:          nil,
			nbOfRecords:  6,
			unRead:       20, // bytes left of other category
		},
		{
			TestCaseName: "CAT048 in end a byte 0xf5 has been removed",
			input:        "",
			err:          ErrUndersized,
			nbOfRecords:  0,
			unRead:       318, // bytes left of other category
		},
		{
			TestCaseName: "CAT034",
			input:        "220014f6083602429b7110940028200094008000",
			err:          nil,
			nbOfRecords:  1,
			unRead:       0,
		},
		{
			TestCaseName: "CAT034",
			input:        "22",
			err:          io.EOF,
			nbOfRecords:  0,
			unRead:       0,
		},
		{
			TestCaseName: "CAT034",
			input:        "220014f6083602429b71109400282000940080", // in end a byte 0x00 has been removed
			err:          ErrUndersized,
			nbOfRecords:  0,
			unRead:       16,
		},
		{
			TestCaseName: "CAT004",
			input:        "04 003d fdcb8008a2080108826ae1800000080001d1c041504d3030303100010bc51ef7a55900f5050370c30c4000003039ff50ffd8a880404cb3820820",
			err:          nil,
			nbOfRecords:  1,
			unRead:       0,
		},
		{
			TestCaseName: "CAT030 STR",
			input:        "",
			err:          nil,
			nbOfRecords:  4,
			unRead:       0,
		},
		{
			TestCaseName: "CAT255 STR",
			input:        "ff000ae008837dfb9c58",
			err:          nil,
			nbOfRecords:  1,
			unRead:       0,
		},
		/*{
			TestCaseName: "CatForTest full record",
			//
			//input:       "1a 0026 FD80 FFFF FFFE AAFFFFFE 02FFFF FFFF 03FFFF 02FFFFFFFF 04FFFFFF 0101FFFF 04FFFFFF",
			input:       "1a 0029 fd 40 ffff fffffe 03ffff 02ffffffff ab80 ff fffe 02ffffffff 04ffffff ffff 0101ffff 03ffff",
			err:         nil,
			nbOfRecords: 1,
			unRead:      0,
		},
		{
			TestCaseName: "CatForTest EOF record",
			input:        "1a 0026 FD80 FFFF FFFE AAFFFFFE 02FFFF FFFF 03FFFF 02FFFFFFFF 04FFFFFF 0101FFFF 05FFFFFF",
			err:          io.ErrUnexpectedEOF,
			nbOfRecords:  1,
			unRead:       0,
		},*/
		{
			TestCaseName: "ErrCategoryUnknown",
			input:        "00 0005FFFF",
			err:          ErrCategoryUnknown,
			nbOfRecords:  0,
			unRead:       0,
		},
		{
			TestCaseName: "CAT063",
			input:        "",
			err:          nil,
			nbOfRecords:  47,
			unRead:       0,
		},
		{
			TestCaseName: "CAT034: over sized data block",
			input:        "220014f6081002412998d89400002000940000811a",
			err:          nil,
			nbOfRecords:  1,
			unRead:       1,
		},
	}

	for _, row := range dataSet {
		// Arrange
		data, _ := util.HexStringToByte(row.input)
		dataB := NewDataBlock()

		// Act
		unRead, err := dataB.Decode(data)

		// Assert
		if err != row.err {
			t.Errorf(util.MsgFailInValue, row.TestCaseName, err, row.err)
		} else {
			t.Logf(util.MsgSuccessInValue, row.TestCaseName, err, row.err)
		}
		if unRead != row.unRead {
			t.Errorf(util.MsgFailInValue, row.TestCaseName, unRead, row.unRead)
		} else {
			t.Logf(util.MsgSuccessInValue, row.TestCaseName, unRead, row.unRead)
		}
		if row.nbOfRecords != len(dataB.Records) {
			t.Errorf(util.MsgFailInValue, row.TestCaseName, len(dataB.Records), row.nbOfRecords)
		} else {
			t.Logf(util.MsgSuccessInValue, row.TestCaseName, len(dataB.Records), row.nbOfRecords)
		}
	}
}

func TestDataBlockPayload(t *testing.T) {
	// Arrange
	data, _ := util.HexStringToByte("30 0118 ")
	nbOfRecords := 7 // cat + len + 5 records
	dataB := new(DataBlock)
	_, _ = dataB.Decode(data)

	// Act
	records := dataB.Payload()

	// Assert
	if len(records) != nbOfRecords {
		t.Errorf("MsgFailInValue: len(items) = %v; Expected: %v", len(records), nbOfRecords)
	} else {
		t.Logf("MsgSuccessInValue: len(items) = %v; Expected: %v", len(records), nbOfRecords)
	}
}

